var $footerNavServices=$body.querySelector(".footer__nav-services");var $footerNavPersonalService=$body.querySelector(".footer__nav-personal-service");var $footerNavAdvantages=$body.querySelector(".footer__nav-advantages");var $footerNavReviews=$body.querySelector(".footer__nav-reviews");//? $products;
var $personalService=$body.querySelector(".other-product");var $advantages=$body.querySelector(".advantages");var $reviews=$body.querySelector(".reviews");function activateFooterList(prm1,prm2,prm3,prm4){if(prm1)activeteScrollButton($footerNavServices,$products);if(prm2)activeteScrollButton($footerNavPersonalService,$personalService,20);if(prm3)activeteScrollButton($footerNavAdvantages,$advantages,20);if(prm4)activeteScrollButton($footerNavReviews,$reviews)}function activeteScrollButton($btn,$element){var numberOfPixelsToMoveUp=arguments.length>2&&arguments[2]!==undefined?arguments[2]:0;$btn.addEventListener("click",function(){window.scrollBy(0,$element.getBoundingClientRect().y-numberOfPixelsToMoveUp)})}